            "status": {
              "type": "string"
            },
            "applicationCount": {
              "type": "integer"
            },
            "maxVolunteers": {
              "type": "integer"
            },
            "acceptedCount": {
              "type": "integer"
            },
            "_id": {
              "type": "string"
            },
            "createdAt": {
              "type": "string"
            },
            "__v": {
              "type": "integer"
            }
          }
        }
      }
    }
  }
}

 ```

